
CREATE TABLE IF NOT EXISTS `#__fb_pages` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `template_id` int(11) NOT NULL,
  `content` longtext,
  `title` varchar(32) NOT NULL,
  `user_id` int(11) NOT NULL,
  `fbuser_id` varchar(32) NOT NULL,
  `fbpage_id` varchar(32) NOT NULL,
  `ordering` int(11) NOT NULL DEFAULT '0',
  `published` tinyint(4) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=6 ;


CREATE TABLE IF NOT EXISTS `#__fb_templates` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(64) NOT NULL,
  `no_block` int(11) NOT NULL,
  `img_block` longtext NOT NULL,
  `video_block` longtext NOT NULL,
  `content_block` longtext NOT NULL,
  `block_html` text NOT NULL,
  `preview_image` varchar(128) NOT NULL,
  `thumb_image` varchar(128) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=3 ;


CREATE TABLE IF NOT EXISTS `#__fb_settings` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `setting_title` varchar(32) NOT NULL,
  `api_id` varchar(128) NOT NULL,
  `api_secret` varchar(128) NOT NULL,
  `ordering` int(11) NOT NULL,
  `published` tinyint(4) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;


CREATE TABLE IF NOT EXISTS `#__fb_system_settings` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `api_id` varchar(128) NOT NULL,
  `api_secret` varchar(128) NOT NULL,
  `license` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=4 ;

